	public void validadeEmails(List<String> emails) {
		ValidationBuilder validationBuilder = Validations.start();
		this.validadeEmail(validationBuilder, emails);
        validationBuilder.evaluateFieldsAndThrows();
	}
	
	public void validadeEmail(ValidationBuilder validationBuilder, List<String> emails) {
		if(emails != null) {
			for(String email: emails) {
				if (StringUtils.isNotBlank(email)) {
					final Validation<String> patternEmailValidation = (object) -> {
						final Pattern ptr = Pattern.compile(PATERN_EMAIL_RFC822);
						return (!ptr.matcher(email).matches());
					};
					validationBuilder.add(Rules.custom(email, patternEmailValidation, "email invalido"));
				}
			}
		}
	}
